    private void jButtonEntrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonEntrarActionPerformed
       String usuario = jTextFieldUsuario.getText(); // pegar texto do campo textField
       char[] password = jPasswordFieldSenha.getPassword(); // pegar senho do campo
       String senha = new String(password);
       
       try{
           // cuidado com a formatacao do QUERY nos parametros do executeQuery
            ResultSet result = statement.executeQuery("SELECT * FROM GC_USUARIOS WHERE login='"+ usuario +"' AND senha=SHA1('"+senha+"')");
            if(result.next()){
                Formulario trabalho = new Formulario();
                this.setVisible(false); // desliga esse...
                trabalho.setVisible(true); // ...e liga esse
            }
            else{
                // mostra msg de error
                JOptionPane.showMessageDialog(this," ","Erro",JOptionPane.ERROR_MESSAGE);
            }
        }
        catch(Exception e){
            // fim do tratamento de erro do SQL Exception
            JOptionPane.showMessageDialog(this,e.getMessage(),"ERRO",JOptionPane.ERROR_MESSAGE);
        }
       
       
    }//GEN-LAST:event_jButtonEntrarActionPerformed

